#!/usr/bin/env python

import sympy

def verify(n, ndps):
  for p in ndps:
    assert n % p != 0, (n, p)
  for p in ndps:
    assert all(f in ndps for f in sympy.ntheory.primefactors(n - p)), (n, p)

